import Card from "./components/ui/Card";
import classes from "./LoginPage.module.css";
import Backdrop from "./components/ui/Backdrop";
import LoginErrorDialog from "./components/ui/LoginErrorDialog";

function LoginPage() {

    const [IsErrorDialogOpen, setIsErrorDialogOpen] = useState(false);

    function showCloseHandler() {
        setIsErrorDialogOpen(true);

    }

    function hideCloseHandler() {
        setIsErrorDialogOpen(false);

    }

    return (
        <div>

            <header className={classes.header}>

                <div className={classes.logo}>Movie Explorer</div>

            </header>

            <Card>
                <div className={classes.welcome_logo}>Welcome to Movie Explorer!</div>
                <div>Login to your account or <a>Sign up</a></div>
                <form>
                    <label>
                        Name:
                        <input type="text" name="name" />
                    </label>
                    <div className={classes.actions} type="submit">
                        <button onClick={showCloseHandler} >Sumbit</button>
                    </div>
                </form>

                {IsErrorDialogOpen && <LoginErrorDialog onCancel={hideCloseHandler} />}
                {IsErrorDialogOpen && <Backdrop onClick={hideCloseHandler} />}

            </Card>
        </div>
    );


}

export default LoginPage
