import './App.css';
import { useEffect } from 'react';

function pickRandomFact(data) {
  var funFactIndex = Math.floor(Math.random() * 2);
  return data.facts[funFactIndex];
}

function App() {

  var currentFact;

  useEffect(() => {
    fetch("/fun_facts").then(reponse =>
      reponse.json().then(data => {
        currentFact = pickRandomFact(data);
      }
      )
    )
  }, []);

  return (
    <div className="App">
      <h1>{currentFact}</h1>

    </div>
  );
}

export default App;
