function LoginForm() {
    return (
        <div className={classes.login}>
            <div className={classes.welcome_logo}>Welcome to Movie Explorer!</div>
            <div>Login to your account or Sign up</div>

            <input className={classes.username} type="text" placeholder='Enter Username' />

            <SumbitButton onClick={showCloseHandler} />

            {IsErrorDialogOpen && <LoginErrorDialog onCancel={hideCloseHandler} />}
            {IsErrorDialogOpen && <Backdrop onClick={hideCloseHandler} />}
        </div>
    );
}

export default LoginForm
