import './App.css';
import { useEffect, useState } from 'react';



function App() {


  var jsonData;

  const [currentFact, setCurrentFact] = useState("");
  const [isButtonClicked, setIsButtonClicked] = useState(true);

  function pickRandomFact() {
    var funFactIndex = Math.floor(Math.random() * 2);
    setCurrentFact(jsonData.facts[funFactIndex]);
    setIsButtonClicked(false);
  }

  function onButtonClicked() {
    setIsButtonClicked(true);
    pickRandomFact();
  }

  useEffect(() => {
    fetch("/fun_facts").then(reponse =>
      reponse.json().then(data => {
        jsonData = data
        currentFact = pickRandomFact();
      }
      )
    )
  }, []);

  return (
    <div className="App">
      {isButtonClicked && <h1>{currentFact}</h1>}
      <button onClick={onButtonClicked}>Get another Fun Fact!</button>

    </div>
  );
}

export default App;
