import { useState } from 'react';
import MovieItem from "../components/MovieItem";
import CommentForm from "../components/CommentForm";
import Card from "../components/ui/js/Card";
import classes from "./MoviePage.module.css"
import card_classes from "../components/ui/css/Card.module.css"


function MoviePage() {


    const [image_source, setImageSource] = useState("");

    const [title, setTitle] = useState("");

    const [tag_line, setTagLine] = useState("");

    const [genre, setGenre] = useState("");

    function getMovieDetails() {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' }
        };
        fetch("/movie/fetch_movie", requestOptions).then(reponse =>
            reponse.json().then(data => {

                setImageSource(data.poster);
                setTitle(data.title);
                setTagLine(data.overview);
                setGenre(data.genre);
            }
            )
        )
    }

    return (
        <div className={classes.flexboxcontainer}>
            <Card stylecard={card_classes.postercard}>
                <MovieItem src={image_source} title={title} tag_line={tag_line} />
            </Card>
            <Card stylecard={card_classes.commentcard}>
                <CommentForm />
            </Card>
        </div>
    );
}


export default MoviePage
