import './App.css';
import { useEffect, useState } from 'react';
import FunFactElement from './components/FunFact';



function App() {

  const [currentFact, setCurrentFact] = useState("");


  function fetchNewFunFact() {
    fetch("/fun_facts").then(reponse =>
      reponse.json().then(data => {
        setCurrentFact(data);
      }
      )
    )

  }

  useEffect(() => { fetchNewFunFact() }, []);

  return (
    <div className="App">
      <FunFactElement text={currentFact} onButtonClicked={useEffect(() => { fetchNewFunFact() }, [])} />

    </div>
  );
}

export default App;
