import './App.css';
import { useEffect, useState } from 'react';



function App() {


  var jsonData;

  const [currentFact, setCurrentFact] = useState("");


  function fetchNewFunFact() {
    fetch("/fun_facts").then(reponse =>
      reponse.json().then(data => {

        setCurrentFact(data.fact);
      }
      )
    )
  }


  useEffect(() => {
    fetchNewFunFact()
  }, []);

  return (
    <div className="App">
      <h1>{currentFact}</h1>
      <button onClick={fetchNewFunFact}>Get another Fun Fact!</button>

    </div>
  );
}

export default App;
