import './App.css';
import { useEffect, useState } from 'react';



function App() {

  function getNewFunFact() {
    var newFact;
    useEffect(() => {
      fetch("/fun_facts").then(reponse =>
        reponse.json().then(data => {
          newFact = data;
        }
        )
      )
    }, []);
    return newFact;
  }



  return (
    <div className="App">
      <h1>{getNewFunFact}</h1>
      <button onClick={getNewFunFact}>Get another Fun Fact!</button>

    </div>
  );
}

export default App;
